
/**
 * Module dependencies.
 */

var assert = require('assert')
  , helpers = require('../helpers');

exports.boundary = '6EA4456247D8.1306949380/Tyler-Holowaychuks-MacBook-Pro.local';

exports.test = function(parser, next){
  parser.on('part', function(part){
    part.header.should.have.property('content-type', 'text/markdown');
    part.header.should.have.property('content-length', '15');
    var buf = '';
    part.on('data', function(chunk){ buf += chunk });
    part.on('end', function(){
      buf.should.equal('some _foo_ bar.');
      next();
    });
  });
};